/**
 * Generic API response wrapper
 *
 * Standard response format for all API endpoints
 */
export interface ApiResponse<T> {
  data: T;
  message: string;
  success: boolean;
}

/**
 * Paginated response wrapper
 *
 * Used for endpoints that return paginated lists of data
 */
export interface PaginatedResponse<T> {
  data: T[];
  total: number;
  page: number;
  limit: number;
  totalPages: number;
}

/**
 * Common table filter parameters
 *
 * Used for search, pagination, and sorting across table components
 */
export interface TableFilters {
  search?: string;
  page?: number;
  limit?: number;
  sortBy?: string;
  sortOrder?: "asc" | "desc";
}

/**
 * Generic select option interface
 *
 * Used for dropdown and select components throughout the application
 */
export interface SelectOption {
  label: string;
  value: string | number;
}
